mapper.select:

SELECT cname
FROM customer
WHERE cid = #{cid} AND cpw = #{cpw}


SELECT hname
FROM hotel
WHERE hid = #{hid} AND hpw = #{hpw}


SELECT ename
FROM emp
WHERE eid = #{eid} AND epw = #{epw}


SELECT cname, firstname, lastname, cmail, cphone
FROM customer
WHERE cid = #{cid}


SELECT ename, email, ephone, position, status, joindate
FROM emp
WHERE eid = #{eid}


SELECT hname, grade, location, hmail, hphone
FROM hotel
WHERE hid = #{hid}


SELECT count(*)
FROM customer
WHERE cid = #{cid}


SELECT count(*)
FROM emp
WHERE eid = #{eid}


SELECT count(*)
FROM hotel
WHERE hid = #{hid}
